import React from "react";
import axios from "axios";



class Newsapi extends React.Component {


  state = {
    articles: [],
    isLoading: true,
    errors: null
  };

  getArticles = () => {
    axios
      .get(
        // "https://newsapi.org/v2/top-headlines?country=in&apiKey=e901d5b623f848939ad3692073bfd152"
        "https://newsapi.org/v2/top-headlines?country=us&apiKey=e901d5b623f848939ad3692073bfd152"
      )
      .then(response => {
        return response.data.articles.map(article => ({
          date: `${article.publishedAt}`,
          title: `${article.title}`,
          url: `${article.url}`,
          image: `${article.urlToImage}`,
          desc: `${article.description}`,
          author: `${article.author}`,
          id: `${article.id}`,
          comm: `${article.comments}`,
          name: `${article.publishedAt}`

        }));
      })
      .then(articles => {
        this.setState({
          articles,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }


  componentDidMount() {
    this.getArticles();

  }

  // handleClick() {
  //   window.location.href="url";
  // }
  //  handleClick = (url) => () => {
  //   window.location.href=url;
  // }

  // handleRedirect = useCallback((url) => {
  //   return () => {
  //     articles.push(url);
  //   }


  render() {
    const { isLoading, articles } = this.state;

    return (
      <React.Fragment>

        <div className="container">
        <h2>Latest News API</h2>
        <div className="row">
          {!isLoading ? (
            articles.map(article => { 
              const { date, title, url, image, desc, author, id, name, cont  } = article;
              // const { id, name, desc, url, cat, lan, country   } = article;
              return (

                <div key={title} className='col-12 col-md-4 col-lg-3 mb-4'  id={id}>  
                <div className="card-group">
                  <div className="card">
                    <img className='img-fluid' src={image} alt='Image' />
                    <div className="card-body">
                      <h4 className="card-title">{title}</h4>
                      <small>{date}</small> <small>{name}</small> 
                      <p className="card-text">{desc}</p>
                      <p className="card-text">{cont}</p>
                       <a className="btn btn-secondary btn-sm" target="_blank" href={url}>Read More </a>
                    </div>
                  <div className="card-footer">
                    <small className="text-muted"><strong>Author</strong>{author}</small>
                  </div>
                </div>  
                </div>
                </div>


              );
            })
          ) : (
            <p>Loading...</p>
          )}
           </div>
        </div>
      </React.Fragment>
    );
  }
}
export default Newsapi;
