import ReactDOM from "react-dom";
import React, { Component, useState, useEffect } from "react";

function EmployeeComponent(){

const emdate = [

    {
        id: 1,
        name: "arjun saini",
        location: "Jaipur",  
        sallery: "4646456",
    },
    {
        id: 2,
        name: "shakir ansari",
        location: "Mumbai",  
        sallery: "4646456"
    },
    {
        id: 3,
        name: "mukul sharma",
        location: "Delhi",  
        sallery: "4646456"
    },
    {
        id: 4,
        name: "manish",
        location: "Pune",  
        sallery: "4646456"
    },


];

// const[items, setItems] =  useState(Menu);

  const [employees,setEmployees]=useState([]);
  const [searchText, setSearchText]=useState('');
  const [employeeCount, setEmployeeCount]=useState(0);













  useEffect(()=>{
    alert('We are inside useEffect Method');
    fetch("employees"+searchText)
      .then(res => res.json())
      .then(
        (result) => {
          setEmployees(result);
        }
      );
  },[searchText,employeeCount]);

  function onSearchTextChange(e){
    setSearchText(e.target.value);
  }

  function addNewEmployee(){
    setEmployeeCount(employeeCount+1);
  }
  return(
    <div>
      <h2>Employees Data...</h2>
      <p>
        <label>Search By Name : <input type="text" value={searchText}
                                  onChange={onSearchTextChange}></input></label>
      </p>  
      <p>
        <button onClick={addNewEmployee}>Add Employee</button>
      </p>    
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Location</th>
            <th>Salary</th>
          </tr>
        </thead>
        <tbody>
          {employees.map(emp=>(
            <tr key={emp.Id}>
              <td>{emp.Id}</td>
              <td>{emp.Name}</td>
              <td>{emp.Location}</td>
              <td>{emp.Salary}</td>
            </tr>
          ))}

        </tbody>
      </table>
    </div>
  )
}

export default EmployeeComponent;
