import React from "react";
import axios from "axios";


class Newsapi extends React.Component {
  state = {
    articles: [],
    isLoading: true,
    errors: null
  };

  getArticles() {
    axios
      .get(
        "https://newsapi.org/v2/everything?q=ai&apiKey=e901d5b623f848939ad3692073bfd152"
      )
      .then(response => {
        return response.data.articles.map(article => ({
          date: `${article.publishedAt}`,
          title: `${article.title}`,
          url: `${article.url}`,
          img: `${article.urlToImage}`,
          desc: `${article.description}`,
          author: `${article.author}`,
        }));
      })
      .then(articles => {
        this.setState({
          articles,
          isLoading: false
        });
      })
      .catch(error => this.setState({ error, isLoading: false }));
  }


  componentDidMount() {
    this.getArticles();
  }

  render() {
    const { isLoading, articles } = this.state;
    return (
      <React.Fragment>
        <h2>News API</h2>
        <div>
          {!isLoading ? (
            articles.map(article => {
              const { date, title, url, img, desc, author  } = article;
              return (
                <div key={title} className='col-4 mx-auto'>  
                <div className="card-group">
                  <div className="card">
                    <img className='img-fluid' src={img} alt='Image' />
                    <div className="card-body">
                      <h4 className="card-title">{title}</h4>
                      <h6 className="card-title">{title}</h6>
                      <p className="card-text">{desc}</p>
                      <p className="card-text">{url}</p>
                    </div>
                  <div className="card-footer">
                    <small className="text-muted"><strong>Author</strong> {author}</small>
                  </div>
                </div>
                </div>
                </div>
              );
            })
          ) : (
            <p>Loading...</p>
          )}
        </div>
      </React.Fragment>
    );
  }
}
export default Newsapi;
