import ReactDOM from "react-dom";
import React, { Component, useState, useEffect } from "react";


const Menu = [

    {
        id: 1,
        image: "images/main-slider1.jpeg",
        name: "arjun saini",
        company: "taggbox",  
        age: "32",
        overview: "Arjun Saini this is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer."
    },
    {
        id: 2,
        image: "images/carousel2.jpg",
        name: "shakir ansari",
        company: "webcontxt",  
        age: "25",
        overview: "Shakir Ansari this is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer."
    },
    {
        id: 3,
        image: "images/webss.webp",
        name: "mukul sharma",
        company: "dreamcast",  
        age: "28",
        overview: "Mukul Sharma this is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer."
    },
    {
        id: 4,
        image: "images/destination1.jpeg",
        name: "amit nagar",
        company: "eventbot",  
        age: "29",
        overview: "Amit Nagar this is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer."
    },

];


function EmployeeComponent(){
  const [employees,setEmployees]=useState([]);
  const [searchText, setSearchText]=useState('');
  const [employeeCount, setEmployeeCount]=useState(0);




//   const [user, setUser] = useState([]);

//   const loadUsers =  async  () => {
//   const result = await  axios.get("http://localhost/ReactApi/view.php");
//   setUser(result.data.phpresult);
//   console.log(result.data.phpresult);

//   };

// useEffect(() => {
// loadUsers();
// }, []);





  useEffect(()=>{
    alert('We are inside useEffect Method');
    fetch("http://localhost/ReactApi/view.php"+searchText)
      .then(res => res.json())
      .then(
        (result) => {
          setEmployees(result);
        }
      );
  },[searchText,employeeCount]);

  function onSearchTextChange(e){
    setSearchText(e.target.value);
  }

  function addNewEmployee(){
    setEmployeeCount(employeeCount+1);
  }
  return(
    <div>
      <h2>Employees Data...</h2>
      <p>
        <label>Search By Name : <input type="text" value={searchText}
                                  onChange={onSearchTextChange}></input></label>
      </p>  
      <p>
        <button onClick={addNewEmployee}>Add Employee</button>
      </p>    
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>Location</th>
            <th>Salary</th>
          </tr>
        </thead>
        <tbody>
          {/* {employees.map(emp=>(
            <tr key={emp.Id}>
              <td>{emp.Id}</td>
              <td>{emp.Name}</td>
              <td>{emp.Location}</td>
              <td>{emp.Salary}</td>
            </tr>
          ))} */}
           {employees.map((res) => 
                    <tr>
                    <th scope="row">{res.username}</th>
                    <td>{res.email}</td>
                    <td>{res.phone}</td>
                    <td>{res.password}</td>
                    </tr>   

                     )}
        </tbody>
      </table>
    </div>
  )
}

export default EmployeeComponent;
